import React, { useState, useEffect } from 'react'
import './Music.sass'
import { ImSpotify } from 'react-icons/im';

function Music() {
    const [token, setToken] = useState();
    const [ref_token, setRefToken] = useState();
    const [name, setName] = useState();
    const [code, setCode] = useState('');

    const clientId = '5523ece294ee4a3a9a70b4b288e6994a';
    const clientSecret = '035176ad27ae4e5483e9c83afafbc325';
    const redirectURL = 'http://localhost:3000/callback';



    const Login = () => {
        const scopes = 'user-read-private user-read-email';
        window.location.href = 'https://accounts.spotify.com/authorize?response_type=code&client_id=' + clientId +
            (scopes ? '&scope=' + encodeURIComponent(scopes) : '') + '&redirect_uri=' + encodeURIComponent(redirectURL);

    }

    // const getToken = async () => {

    //     const result = await fetch('https://accounts.spotify.com/api/token', {
    //         method: 'POST',
    //         headers: {
    //             'Content-Type': 'application/x-www-form-urlencoded',
    //             'Authorization': 'Basic ' + btoa(clientId + ':' + clientSecret)
    //         },
    //         body: {
    //             'grant_type': 'authorization_code',
    //             'code': '/authorize endpoint',
    //             'redirect_uri': 
    //         }
    //     });
    //     const data = await result.json();
    //     return data.access_token;
    // }
    const _getToken = async () => {
        const result = await fetch('https://accounts.spotify.com/api/token', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/x-www-form-urlencoded',
                'Authorization': 'Basic ' + btoa(clientId + ':' + clientSecret)
            },
            body: `grant_type=authorization_code&code=${code}&redirect_uri=${redirectURL}`,
        }).then(res => res.json()).then((data) => { setToken(data.access_token); setRefToken(data.refresh_token); console.log(data) })

    }

    const getNowSong = async () => {

        const result = await fetch(`https://api.spotify.com/v1/me/player`, {
            method: 'GET',
            headers: {
                'Authorization': 'Bearer ' + token
            },
            //scopes: 'user-read-currently-playing',
        }).then(res => res.json()).then((data) => console.log(data));
    }

    const _getTrack = async () => {
        const result = await fetch(`https://api.spotify.com/v1/tracks/6hTcRuSaaTNwlyIYkkMpWX`, {
            method: 'GET',
            headers: {
                'Authorization': 'Bearer ' + token
            }
        }).then(res => res.json()).then(data => { setName(data.name) })
        console.log(name);
    }


    useEffect(() => {
        let hash = document.URL.split('=')[1];
        setCode(hash);
        _getToken();
    }, []);

    return (
        <div className='music'>
            <div className='music-loggedin'>
                Logged in
            </div>
            <div className='music-loggedout'>
                <div className='music-loggedout-icon' onClick={() => { console.log(token); console.log(code); _getToken() }}>
                    <ImSpotify />
                </div>
                <div className='music-loggedout-button' onClick={() => Login()}>
                    Log in
                </div>
                <div>
                    1
                </div>
                <div>
                    2
                </div>
                <div>
                    3
                </div>
                <div>
                    {name}
                </div>
            </div>
        </div >
    )
}


export default Music
