        //         (scopes ? '&scope=' + encodeURIComponent(scopes) : '') + '&redirect_uri=' + encodeURIComponent(redirectURL);
        // }

        const Login = async () => {

            const result = await fetch('https://accounts.spotify.com/authorize' + new URLSearchParams({
                client_id: clientId,
                response_type: 'code',
                redirect_uri: redirectURL,
                scope: 'user-read-private user-read-email'
            }), {
                method: 'GET'
            });
        }

        const getCategories = () => {

        }

        Login();
        console.log(getToken());
    }


    return (
        <div className='music'>
            <div className='music-loggedin'>
                Logged in
            </div>
            <div className='music-loggedout'>
                <div className='music-loggedout-icon'>
                    <ImSpotify />
                </div>
                <div className='music-loggedout-button' onClick={SpotifyLogin}>
                    Log in
                    </div>
            </div>
        </div>
    )
}


export default Music
