import React, { useEffect, useRef, useState } from 'react'
import './WindowView.sass'
import { IoMdRemove, IoMdSquareOutline, IoMdClose } from 'react-icons/io';
import { BiWindows, BiWindow } from 'react-icons/bi';
import { useDispatch, useSelector } from 'react-redux';
import { selectFolderOpen, setFolderOpen } from '../../Redux/appSlice';

function WindowView() {
    const folderOpen = useSelector(selectFolderOpen)
    const [screenMode, setScreenMode] = useState({
        display: 'none',
        height: '250',
        width: '500',
        top: 220,
        left: 480,
        border: '1px'
    })
    const [off, setOff] = useState({ x: 0, y: 0 })
    const [isWindowMoving, setIsWindowMoving] = useState(false);
    const [isWindowResizeing, setIsWindowResizeing] = useState(false);
    const dispatch = useDispatch();
    const ref = useRef<HTMLDivElement>(null)
    const windowSize = (e: any) => {
        if (screenMode.height == '50') {
            setScreenMode({ ...screenMode, height: '100', width: '100', top: 0, left: 0, border: '0px' })
        } else {
            setScreenMode({ ...screenMode, height: '50', width: '50', top: 220, left: 480, border: '1px' })
        }
    }

    const handleWindowMove = (event: any) => {
        let x = event.clientX - off.x;
        let y = event.clientY - off.y;
        console.log(x + ':' + event.width)
        setScreenMode({ ...screenMode, top: y, left: x })
    };

    const handleWindowResize = (event: any) => {

        let x = event.clientX;
        let y = event.clientY;
        console.log(x + ':' + y)
        setScreenMode({ ...screenMode, height: String(y), width: String(x) })
    };

    const getMoveMouse = (e: any) => {
        setOff({ x: e.nativeEvent.offsetX, y: e.nativeEvent.offsetY })
    }

    const getResizeMouse = (e: any) => {
        setOff({ x: 1, y: 1 })
    }

    useEffect(() => {
        if (isWindowMoving) window.addEventListener("mousemove", handleWindowMove);
        else window.removeEventListener("mousemove", handleWindowMove);
        return () => window.removeEventListener("mousemove", handleWindowMove);
    }, [isWindowMoving, handleWindowMove]);

    useEffect(() => {
        if (isWindowResizeing) window.addEventListener("mousemove", handleWindowResize);
        else window.removeEventListener("mousemove", handleWindowResize);
        return () => window.removeEventListener("mousemove", handleWindowResize);
    }, [isWindowResizeing, handleWindowResize]);

    useEffect(() => {
        if (folderOpen) setScreenMode({ ...screenMode, display: 'flex' });
        else setScreenMode({ ...screenMode, display: 'none' });
        return () => setScreenMode({ ...screenMode, display: 'none' });
    }, [screenMode.display, folderOpen]);

    useEffect(() => {
        console.log('width', ref.current ? ref.current.offsetWidth : 0);
    }, [ref.current, folderOpen]);

    return (
        <div className='windowview' ref={ref} style={{ display: screenMode.display, height: screenMode.height + 'px', width: screenMode.width + 'px', top: screenMode.top + 'px', left: screenMode.left + 'px', borderWidth: screenMode.border }}>
            <div className='windowview-toolbar' onMouseDown={(e) => { getMoveMouse(e); setIsWindowMoving(true) }} onMouseUp={() => { setIsWindowMoving(false) }}>
                <div></div>
                <div className='windowview-toolbar-buttons'>
                    <div className='windowview-toolbar-buttons-minimize' onClick={() => { dispatch(setFolderOpen({ folderOpen: !folderOpen })) }} onMouseDown={(e) => { e.stopPropagation() }}>
                        <IoMdRemove />
                    </div>
                    <div className='windowview-toolbar-buttons-maximize' onClick={(e) => windowSize(e)} onMouseDown={(e) => { e.stopPropagation() }}>
                        {
                            screenMode.height == '100' ?
                                <BiWindows /> :
                                <BiWindow />
                        }
                    </div>
                    <div className='windowview-toolbar-buttons-close' onClick={() => { dispatch(setFolderOpen({ folderOpen: !folderOpen })) }} onMouseDown={(e) => { e.stopPropagation() }}>
                        <IoMdClose />
                    </div>
                </div>
            </div>
            <div className='windowview-body'>

            </div>
            <div className='windowview-scale' onClick={() => { }} onMouseDown={(e) => { getResizeMouse(e); setIsWindowResizeing(true) }} onMouseUp={() => { setIsWindowResizeing(false) }}></div>
        </div>
    )
}

export default WindowView
