import React from 'react'
import './Music.sass'
import { ImSpotify } from 'react-icons/im';

function Music() {

    const SpotifyLogin = () => {

        const express = require('express');
        const request = require('request'); // "Request" library
        const cors = require('cors');
        const querystring = require('querystring');
        const cookieParser = require('cookie-parser');

        const app = express();

        app.use(express.static(__dirname + '/public'))
            .use(cors())
            .use(cookieParser());

        var client_id = '5523ece294ee4a3a9a70b4b288e6994a'; // Your client id
        var client_secret = '035176ad27ae4e5483e9c83afafbc325'; // Your secret
        var redirect_uri = 'http://localhost:3000'; // Your redirect uri

        app.get('/login', function (req: any, res: any) {
            var scopes = 'user-read-private user-read-email';
            res.redirect('https://accounts.spotify.com/authorize' +
                '?response_type=code' +
                '&client_id=' + client_id +
                (scopes ? '&scope=' + encodeURIComponent(scopes) : '') +
                '&redirect_uri=' + encodeURIComponent(redirect_uri));
        });
    }


    return (
        <div className='music'>
            <div className='music-loggedin'>
                Logged in
            </div>
            <div className='music-loggedout'>
                <div className='music-loggedout-icon'>
                    <ImSpotify />
                </div>
                <div className='music-loggedout-button' onClick={SpotifyLogin}>
                    Log in
                    </div>
            </div>
        </div>
    )
}


export default Music
