import React, { useState } from 'react'
import './Weather.sass'

function Weather() {
    const [temp, setTemp] = useState(0);
    const [description, setDescription] = useState('l');
    const [icon, setIcon] = useState(0);

    const key: string = "ef5b7ec63216009ee88d661f0ad92943"
    const kelvin: number = 273;

    if ('geolocation' in navigator) {
        navigator.geolocation.getCurrentPosition(setPosition, showError);
    } else {
        alert('Browser doesnt support geolocation');
    }

    function setPosition(position: any): any {
        let latitude = position.coords.latitude
        let longitude = position.coords.longitude

        getWeather(latitude, longitude);
    }

    function showError() {
        alert('error');
    }

    function getWeather(latitude: number, longitude: number): any {
        let api = `api.openweathermap.org/data/2.5/weather?lat=${latitude}&lon=${longitude}&appid=${key}`;
        // let api = `api.openweathermap.org/data/2.5/weather?lat=51.50885&lon=-0.12658&appid=${key}`;

        fetch(api)
            .then(function (response) {
                let data = JSON.parse(response.text());
                console.dir(data);
                return data;
            })
            .then(function (data) {
                setTemp(Math.floor(data.main.temp - kelvin));
                setDescription(data.weather[0].description);
                setIcon(data.weather[0].icon);
            })
    }


    return (
        <div className='weather'>
            <div>{icon}</div>
            <div>
                <div>{temp}</div>
                <div>{description}</div>
            </div>
        </div>
    )
}

export default Weather
