import React from 'react'
import './Music.sass'
import { ImSpotify } from 'react-icons/im';

function Music() {

    const SpotifyLogin = () => {

        function getHashParams() {
            let hashParams: any = {};
            var e, r = /([^&;=]+)=?([^&;]*)/g,
                q = window.location.hash.substring(1);
            while (e = r.exec(q)) {
                hashParams[e[1]] = decodeURIComponent(e[2]);
            }
            return hashParams;
        }
        var params = getHashParams();

        var access_token = params.access_token,
            refresh_token = params.refresh_token,
            error = params.error;

        if (error) {
            alert('There was an error during the authentication');
        } else {
            if (access_token) {
                console.log('access_token work')

                access_token.ajax({
                    url: 'https://api.spotify.com/v1/me',
                    headers: {
                        'Authorization': 'Bearer ' + access_token
                    },
                    success: function (response: any) {
                        console.log(response)
                    }
                });
            } else {
                console.log('back to login screen')
            }
        }
    }


    return (
        <div className='music'>
            <div className='music-loggedin'>
                Logged in
            </div>
            <div className='music-loggedout'>
                <div className='music-loggedout-icon'>
                    <ImSpotify />
                </div>
                <div className='music-loggedout-button' onClick={SpotifyLogin}>
                    Log in
                    </div>
            </div>
        </div>
    )
}


export default Music
