import React, { useState, useEffect } from 'react'
import './Music.sass'
import { ImSpotify } from 'react-icons/im';

function Music() {
    const [token, setToken] = useState();
    const [name, setName] = useState();
    const [code, setCode] = useState();

    const clientId = '5523ece294ee4a3a9a70b4b288e6994a';
    const clientSecret = '035176ad27ae4e5483e9c83afafbc325';
    const redirectURL = 'http://localhost:3000';



    const Login = () => {
        const scopes = 'user-read-private user-read-email';
        window.location.href = 'https://accounts.spotify.com/authorize?response_type=code&client_id=' + clientId +
            (scopes ? '&scope=' + encodeURIComponent(scopes) : '') + '&redirect_uri=' + encodeURIComponent(redirectURL);
    }

    const getLogin = async () => {
        const scopes = 'user-read-private user-read-email';
        const result = await fetch('https://accounts.spotify.com/authorize?response_type=code&client_id=' + clientId +
            (scopes ? '&scope=' + encodeURIComponent(scopes) : '') + '&redirect_uri=' + encodeURIComponent(redirectURL), {
            method: 'GET',
            headers: {
                'Content-Type': 'application/x-www-form-urlencoded',
            },
            mode: 'cors',
            credentials: 'same-origin'
        }).then(res => res).then((data) => console.log(data))
    }

    // const getToken = async () => {

    //     const result = await fetch('https://accounts.spotify.com/api/token', {
    //         method: 'POST',
    //         headers: {
    //             'Content-Type': 'application/x-www-form-urlencoded',
    //             'Authorization': 'Basic ' + btoa(clientId + ':' + clientSecret)
    //         },
    //         body: {
    //             'grant_type': 'authorization_code',
    //             'code': '/authorize endpoint',
    //             'redirect_uri': 
    //         }
    //     });
    //     const data = await result.json();
    //     return data.access_token;
    // }
    const _getToken = async () => {
        const result = await fetch('https://accounts.spotify.com/api/token', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/x-www-form-urlencoded',
                'Authorization': 'Basic ' + btoa(clientId + ':' + clientSecret)
            },
            //body: 'grant_type=client_credentials',
            body: JSON.stringify({
                grant_type: 'authorization_code',
                code: code,
                redirect_uri: redirectURL
            }),
        }).then(res => res.json()).then((data) => { setToken(data.access_token) }).catch(error => console.log(error.message));

        // const data = await result.json();
        // setToken(data.access_token);
        // return data.access_token;
    }

    const getNowSong = async () => {

        const result = await fetch(`https://api.spotify.com/v1/albums/2UQlrrIJQglk4uWWTwvREu/tracks`, {
            method: 'GET',
            headers: {
                'Authorization': 'Bearer ' + token
            },
            //scopes: 'user-read-currently-playing',
        }).then(res => res.json()).then((data) => console.log(data));
        console.log(token)
    }

    const _getTrack = async () => {
        const result = await fetch(`https://api.spotify.com/v1/tracks/6hTcRuSaaTNwlyIYkkMpWX`, {
            method: 'GET',
            headers: {
                'Authorization': 'Bearer ' + token
            }
        }).then(res => res.json()).then(data => { setName(data.name) })
        // const data = await result.json();
        // return data.name;
        console.log(name);
    }

    //_getTrack();

    const getCode = async () => {
        const result = await fetch("http://localhost:3000/?code=AQAB4ojoJvCPI9eQhy4oCsfVCXtNiqoLIAR0Y-TSWHs0zp84s2SSjjAcT5_zlxyu72-eg4LCkbr7z5HRta-CTqeNaYczc3PAAnQCz6L_Q4wrVYd7WDBizXEtQPAHN9i80d7l2oJ6fSGsrP7XdP0Cl4-q6pCKW6ZFWWoTuWuJHCHu3fPDpUSpt_yCg7I8L0_r8TkXJvgMLnflTePILw", {
            headers: {
                "accept": "text/html,application/xhtml+xml,application/xml;q=0.9,image/avif,image/webp,image/apng,*/*;q=0.8,application/signed-exchange;v=b3;q=0.9",
                "accept-language": "pl-PL,pl;q=0.9,en-US;q=0.8,en;q=0.7",
                "cache-control": "no-cache",
                "pragma": "no-cache",
                "sec-fetch-dest": "document",
                "sec-fetch-mode": "navigate",
                "sec-fetch-site": "cross-site",
                "sec-fetch-user": "?1",
                "upgrade-insecure-requests": "1"
            },
            referrer: "http://localhost:3000/",
            referrerPolicy: "strict-origin-when-cross-origin",
            body: null,
            method: "GET",
            mode: "cors",
            credentials: "omit"
        });
    }

    useEffect(() => {
        _getToken();
        console.log(token);
    }, []);

    return (
        <div className='music'>
            <div className='music-loggedin'>
                Logged in
            </div>
            <div className='music-loggedout'>
                <div className='music-loggedout-icon' onClick={getCode}>
                    <ImSpotify />
                </div>
                <div className='music-loggedout-button' onClick={() => getNowSong()}>
                    Log in
                </div>
                <div>
                    {name}
                </div>
            </div>
        </div>
    )
}


export default Music
