import React from 'react'
import './Weather.sass'

function Weather() {

    const weather = {
        temperature: {
            value: 0,
            unit: 'celsius'
        },
        description: '',
        iconId: 0,
    };

    const key: string = '82005d27a116c2880c8f0fcb866998a0';
    const kelvin: number = 273;

    const showError = () => {
        alert('error');
    }

    if ('geolocation' in navigator) {
        navigator.geolocation.getCurrentPosition(setPosition, showError);
    } else {
        alert('Browser doesnt support geolocation');
    }

    function setPosition(position: any): any {
        let latitude = position.coords.latitude
        let longitude = position.coords.longitude

        getWeather(latitude, longitude);
    }

    const getWeather = (latitude: number, longitude: number) => {
        let api = `http://api.openweathermap.org/data/2.5/weather?lat=${latitude}&lon=${longitude}&appid=${key}`;

        fetch(api)
            .then((response) => {
                let data = response.json();
                return data;
            })
            .then((data) => {
                if (data.main.temp) {
                    weather.temperature.value = Math.floor(data.main.temp - kelvin);
                    weather.description = data.weather[0].description;
                    weather.iconId = data.weather[0].icon;
                }
            })
    }


    return (
        <div className='weather' onClick={() => alert(weather.iconId)}>

        </div>
    )
}

export default Weather
